$(function() {
	$('.js-hero-carousel').slick({
		arrows: false,
		dots: true,
		appendDots: $('.hero-slider__dots'),
		infinite: true,
		slidesToShow: 1,
		slidesToScroll: 1,
		autoplay: true,
		autoplaySpeed: 5000
	});

	$('.js-tours-carousel').slick({
		dots: false,
		prevArrow: $('.hot-tours-slider__button_prev'),
		nextArrow: $('.hot-tours-slider__button_next')
	});

	$('.js-reviews-carousel').slick({
		dots: false,
		adaptiveHeight: true,
		prevArrow: $('.reviews__button_prev'),
		nextArrow: $('.reviews__button_next')
	})



	function formatState (state) {
		if (!state.id) {
			return state.text;
		}
		var $state = $(
			'<span><img src="' + state.element.getAttribute('data-flag') + '" class="img-flag" /> ' + state.text + '</span>'
		);
		return $state;
	};

	$('.js-filter-country').select2({
		minimumResultsForSearch: Infinity,
		templateResult: formatState,
		templateSelection: formatState
	});

	$('.js-filter-country').on('change', function() {
		var thisCountry = $(this).val();
		$('.popular-tours__country-categories').removeClass('active');
		$('.popular-tours__country-categories[data-country="' + thisCountry + '"]').addClass('active');
		$('.popular-tours__country-categories[data-country="' + thisCountry + '"] button:first-child').click();

		$('.popular-tours__country-layer').removeClass('popular-tours__country-layer_active');
		$('.popular-tours__country-layer[data-country="' + thisCountry + '"]').addClass('popular-tours__country-layer_active');
	});

	$('.popular-tours__category-button').on('click', function() {
		var thisCountry = $(this).parents('.popular-tours__country-categories').data('country'),
			thisCategory = $(this).data('category');
		if(!$(this).hasClass('active')) {
			$('.popular-tours__country-layer[data-country="' + thisCountry + '"] .tour-cards__page').removeClass('tour-cards__page_active');
			$('.popular-tours__country-layer[data-country="' + thisCountry + '"] .tour-cards__page[data-category="' + thisCategory + '"]').addClass('tour-cards__page_active');
			$(this).addClass('active').siblings('button').removeClass('active');
		}
	});

	$('.visa__countries button').on('click', function() {
		var thisCountry = $(this).data('country');

		$('.visa__form').find('[name="country_id"]').val($(this).hasClass('active') ? '' : thisCountry);
		if ($(this).hasClass('active')) {
			$(this).removeClass('active');
		} else {
			$(this).addClass('active').siblings('button').removeClass('active');
		}

		$('.visa__info[data-country="' + thisCountry + '"]')
			.slideToggle(200)
			.siblings('.visa__info')
			.slideUp(200);
	})

	// Floating menu

	$('.red-menu').sticky();

	$('.red-menu a, .go-top-button').on('click', function(e) {
		e.preventDefault();
		var thisHref = $(this).attr('href'), top = $(thisHref).offset().top;

		$('html, body').animate({
			scrollTop: top > 40 ? top - 40 : 0
		}, 300);
	});

	// PickMeUp

	var pmuLocale = {
		days		: ['', '', '', '', '', '', '', ''],
		daysShort	: ['', '', '', '', '', '', '', ''],
		daysMin		: ['', '', '', '', '', '', '', ''],
		months		: ['', '', '', '', '', '', '', '', '', '', '', ''],
		monthsShort	: ['', '', '', '', '', '', '', '', '', '', '', '']
	}

	$('.hot-form input.date').each(function(){
		$(this).pickmeup({
			position : 'bottom',
			hide_on_select: true,
			min : new Date,
			locale : pmuLocale
		});
	});

	$(window).scroll(function(){
		var btn = $('.go-top-button');
		if ($(window).scrollTop()){
			if (btn.is(':hidden')) btn.stop().fadeIn();
		}
		else if (btn.is(':visible')){
			btn.stop().fadeOut();
		}
	});
});

